function createCookie(e,t,n){if(n){var o=new Date;o.setTime(o.getTime()+24*n*60*60*1e3);var r="; expires="+o.toGMTString()}else var r="";document.cookie=e+"="+t+r+"; path=/"}function readCookie(e){for(var t=e+"=",n=document.cookie.split(";"),o=0;o<n.length;o++){for(var r=n[o];" "==r.charAt(0);)r=r.substring(1,r.length);if(0==r.indexOf(t))return r.substring(t.length,r.length)}return null}function eraseCookie(e){createCookie(e,"",-1)}


jQuery(document).ready(function($) {

	//global variables
	var $body = $('body'),
	$wind = $(window),
	windW = $wind.width(),
	windH = $wind.height();


	//init functions
    cookies_popup();
	lazy_load();
	owl_init();
	mobile_menu();


    //cookies pop-up
	function cookies_popup() {

		var cookie_section = $('#cookies-section');

		// define cookie name (replace 'ThemePrefix' with the desired text)
		var name = 'onroad_cookie_info_popup';

		// reed the cookie status
	        var status = readCookie(name);

	        // Show info if cookie !exist
	        if (status == null) {
	            cookie_section.show();
	        }

	        // set cookie value 1, save cookie for 30days and hide cookie section
	        cookie_section.on('click', function(){

	            var value = 1;
	            var days = 30;

	            createCookie(name,value,days);

	            cookie_section.slideUp();

	        });

	}


	//b-lazy lazy load
	function lazy_load(){
		var blazy_item = $('.b-lazy');
		if (blazy_item.length > 0){var bLazy = new Blazy(); }
	}


	//slider owl
	function owl_init(){
		$('.owl-carousel').owlCarousel({
			items: 1,
		    loop: true,
		    autoplay: true,
		    autoplayTimeout: 4000,
		    autoplayHoverPause: true,
		    nav: true,
		});
    }


	//masonry
	function masonry_init(){
		var gallery = $('.gallery');

		gallery.each(function(){

			$(this).masonry({
				itemSelector: '.category-item',
			});

		});

	}


	//mobile menu toggler
	function mobile_menu(){

		var btn = $('#menu-toggle, .sidebar-btn'),
			menu = $('.menu-primary');
			mobile_menu = $('#mobile-menu');
            sidebar_menu = $('.sidebar-menu');

		btn.on('click', function(){
			$body.toggleClass('menu-open');
            sidebar_menu.toggleClass('opened');
		});


		mobile_menu.find('li.menu-item-has-children').each(function(){
			$(this).prepend('<span class="nav-more">+</span>');
		});

		var nav_more = $('.nav-more');
		nav_more.on('click', function(){
			$(this).siblings('ul.sub-menu').toggleClass('active').slideToggle();
		});

	}


});
